 var path = 'hr' + 'ef' + '=';
 var addy31470 = 'g&#105;&#97;&#111;d&#117;c' + '&#64;';
 addy31470 = addy31470 + 'z&#105;ng' + '&#46;' + 'vn' + '&#46;' + '';
 document.write( '<a ' + path + '\'' + prefix + ':' + addy31470 + '\'>' );
 document.write( addy31470 );
